    wire [7:0] ad1_1, ad1_2;

    // Instruction register for port 1
    ins_p1 ins1_module (
        .clk(clk),
        .rst(rst),
        .fetch(fetch),
        .data(data),
        .ins(ins1),
        .ad1(ad1_1)
    );

