        .clk(clk),
        .rst(rst),
        .fetch(fetch),
        .data(data),
        .ins(ins2),
        .ad1(ad1_2)
    );

    // Output assignments
    assign ins = (fetch == 2'b01) ? ins1 : (fetch == 2'b10) ? ins2 : {3{1'bx}};
    assign ad1 = (fetch == 2'b01) ? ad1_1 : (fetch == 2'b10) ? ad1_2 : {5{1'bx}};
    assign ad2 = (fetch == 2'b01) ? {8{1'bx}} : (fetch == 2'b10) ? data : {8{1'bx}};

endmodule

module ins_p1 (
    input clk,
    input rst,
    input [1:0] fetch,
    input [7:0] data,
    output reg [2:0] ins,
    output reg [4:0] ad1
);
    always @(posedge clk or negedge rst) begin
        if (!rst) begin
            ins <= 3'bxxx;
            ad1 <= 5'bxxxxx;
        end else begin
            if (fetch == 2'b01) begin
                ins <= data[7:5];
                ad1 <= data[4:0];
            end
        end
    end
endmodule

module ins_p2 (
    input clk,
    input rst,
    input [1:0] fetch,
    input [7:0] data,
    output reg [2:0] ins,
    output reg [7:0] ad2
);
    always @(posedge clk or negedge rst) begin
        if (!rst) begin
            ins <= 3'bxxx;
            ad2 <= 8'bxxxxxxxx;
        end else begin
            if (fetch == 2'b10) begin
                ins <= data[7:5];
                ad2 <= data;
            end
        end
