always @(posedge CLK_in or posedge RST) begin
    if (RST) begin
        counter <= 0;
        PWM_out <= 0;
    end else begin
        counter <= counter + 1;
        if (counter == 127) begin
            PWM_out <= 1;
            counter <= 0;
        end else if (counter == 255) begin
            PWM_out <= 0;
            counter <= 0;
        end
    end
end

