        .a(a),
        .b(b),
        .borrow_out(sub_borrow),
        .difference(sub_result)
    );

    assign carry_borrow = (op == 2'b10) ? add_carry : sub_borrow;

    assign result = (op == 2'b00) ? and_result :
                  (op == 2'b01) ? or_result :
                  (op == 2'b10) ? add_result :
