module state_control (
    input clk,
    input rst_n,
    output reg [1:0] state
);

    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            state <= 2'b00;
        end else if (state == 2'b00) begin
            state <= 2'b01;
        end else if (state == 2'b01) begin
            state <= 2'b10;
        end else begin
            state <= 2'b00;
        end
    end

endmodule

module waveform_generator (
    input clk,
    input rst_n,
    input [1:0] state,
    output reg [4:0] wave
);

    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            wave <= 5'b000000;
        end else begin
            case (state)
                2'b00: wave <= wave + 1;
                2'b01: wave <= wave - 1;
                default: wave <= 5'b000000;
            endcase
        end
    end

endmodule

