module pwm_25 (
    input CLK_in,
    input RST,
    output reg PWM_on
);
    parameter CYCLE_COUNT = 40000000; // 25% duty cycle

    reg [31:0] count = 0;

    always @(posedge CLK_in or posedge RST) begin
        if (RST) begin
            count <= 0;
            PWM_on <= 0;
        end else begin
            count <= count + 1;
            if (count < CYCLE_COUNT / 4) begin
                PWM_on <= 1;
            end else if (count >= CYCLE_COUNT) begin
                PWM_on <= 0;
                count <= 0;
            end
        end
    end
endmodule

