    end
  end
endmodule

module decode_stage(
  input [31:0] instr,
  output reg [31:0] decoded_instr
);
  always @(*) begin
    decoded_instr = instr;
  end
endmodule

module execute_stage(
  input [31:0] instr,
  output reg [31:0] alu_out
);
  always @(*) begin
    case (instr[31:30])
      'h000: alu_out = instr[31:20] + instr[19:10];
      'h001: alu_out = instr[31:20] - instr[19:10];
      default: alu_out = 0;
    endcase
  end
endmodule

module memory_stage(
  input [31:0] instr,
  output reg [31:0] mem_out
);
  always @(*) begin
    mem_out = instr;
  end
endmodule

module writeback_stage(
  input [31:0] instr,
  input [31:0] res_reg_file [0:31],
  output reg [31:0] new_res_reg_file [0:31]
);
  always @(*) begin
    new_res_reg_file[instr[15:12]] = res_reg_file[instr[15:12]] + instr[31:20];
  end
endmodule

