    output [16:1] S;
    output C_out;

    wire [15:0] C;

    // Instantiate 4-bit CLA instances
    CLA add1(A[15:0], B[15:0], C[0], S[15:0]);
    CLA add2(A[14:1], B[14:1], C[1], S[14:1]);
    CLA add3(A[13:2], B[13:2], C[2], S[13:2]);
    CLA add4(A[12:3], B[12:3], C[3], S[12:3]);

    // Final carry out is the carry out of the 4-bit CLA
    assign C_out = C[3];
endmodule

module CLA(A, B, C_in, S, C_out);
    input [3:0] A, B;
    input C_in;
    output [3:0] S;
    output C_out;

    wire [3:0] G, P, C;

    // Generate G and P values
    assign G = A & B;
    assign P = A ^ B;

    // Calculate C values
    assign C[0] = C_in;
    assign C[1] = G[0] | (P[0] & C[0]);
    assign C[2] = G[1] | (P[1] & C[1]);
    assign C[3] = G[2] | (P[2] & C[2]);

    // Calculate S values
    assign S[0] = P[0] ^ C[0];
    assign S[1] = P[1] ^ C[1];
    assign S[2] = P[2] ^ C[2];
    assign S[3] = P[3] ^ C[3];

    // Output carry out
    assign C_out = C[3];
endmodule

