        .fetched_instr(fetched_instr)
    );

    // Instantiate execute stage
    execute_stage execute (
        .clk(clk),
        .rst(rst),
        .fetched_instr(fetched_instr),
        .reg_file(reg_file),
        .decoded_instr(decoded_instr),
        .result(result)
    );

    // Instantiate writeback stage
    writeback_stage writeback (
        .clk(clk),
        .rst(rst),
        .decoded_instr(decoded_instr),
        .reg_file(reg_file),
        .out_reg_file(out_reg_file)
    );
endmodule

module fetch_stage(
    input clk,
    input rst,
    input [31:0] instr_mem [0:31],
    output reg [31:0] fetched_instr
);
    reg [4:0] pc;

    always @(posedge clk or posedge rst) begin
        if (rst) begin
            pc <= 0;
        end else begin
            pc <= pc + 1;
        end
    end

    assign fetched_instr = instr_mem[pc];
endmodule

module execute_stage(
    input clk,
    input rst,
    input [31:0] fetched_instr,
    input [31:0] reg_file [0:31],
    output reg [31:0] decoded_instr,
    output reg [31:0] result
);
    wire [4:0] rs1, rs2;
    wire [6:0] imm;
    wire [1:0] opcode;

    assign rs1 = fetched_instr[25:21];
    assign rs2 = fetched_instr[20:16];
    assign imm = fetched_instr[15:7];
    assign opcode = fetched_instr[31:30];

    always @(posedge clk or posedge rst) begin
        if (rst) begin
            decoded_instr <= 0;
            result <= 0;
        end else begin
            case (opcode)
                2'b00: decoded_instr <= reg_file[rs1] + reg_file[rs2];
                2'b01: decoded_instr <= reg_file[rs1] - reg_file[rs2];
                default: decoded_instr <= 0;
            endcase
            result <= decoded_instr + imm;
        end
    end
endmodule

module writeback_stage(
    input clk,
    input rst,
    input [31:0] decoded_instr,
    input [31:0] reg_file [0:31],
    output reg [31:0] out_reg_file [0:31]
);
    wire [4:0] rd;

    assign rd = decoded_instr[20:16];

    always @(posedge clk or posedge rst) begin
        if (rst) begin
            out_reg_file <= 0;
        end else begin
            out_reg_file[rd] <= decoded_instr;
        end
    end
endmodule

