module execute_stage(
    input [31:0] instr,
    input [31:0] reg_file [0:31],
    output [31:0] decoded_instr,
    output [31:0] result
);
    wire [4:0] opcode;
    wire [4:0] rs1;
    wire [4:0] rs2;
    wire [4:0] rd;
    wire [31:0] val1;
    wire [31:0] val2;
    wire [31:0] res;

    assign opcode = instr[31:27];
    assign rs1 = instr[26:22];
    assign rs2 = instr[21:17];
    assign rd = instr[16:12];

    assign val1 = reg_file[rs1];
    assign val2 = reg_file[rs2];

    assign decoded_instr = instr;

    always @(*) begin
        case (opcode)
            // Addition
            5'b000000: res = val1 + val2;
            // Subtraction
            5'b000010: res = val1 - val2;
            default: res = 0;
        endcase
    end

    assign result = res;
endmodule

