module execute_stage(
    input clk,
    input rst,
    input [31:0] instr,
    input [31:0] reg_file [0:31],
    output reg [31:0] decoded_instr,
    output reg [31:0] result
);
    wire [4:0] rs1, rs2;
    wire [6:0] opcode;
    wire [4:0] dest;
    wire [4:0] sa;

    assign rs1 = instr[26:22];
    assign rs2 = instr[21:17];
    assign opcode = instr[31:27];
    assign dest = instr[16:12];
    assign sa = instr[11:6];

    always @(posedge clk or posedge rst) begin
        if (rst) begin
            decoded_instr <= 0;
            result <= 0;
        end else begin
            case (opcode)
                "000000": // ADD
                    result <= reg_file[rs1] + reg_file[rs2];
                "000001": // SUB
                    result <= reg_file[rs1] - reg_file[rs2];
                default:
                    result <= 0;
            endcase
            decoded_instr <= {sa, rs2, rs1, dest, opcode};
        end
    end
endmodule

module writeback_stage(
    input clk,
    input rst,
    input [31:0] instr,
    input [31:0] reg_file [0:31],
    output reg [31:0] out_reg_file [0:31]
);
    wire [4:0] dest;
    wire [4:0] sa;

    assign dest = instr[16:12];
    assign sa = instr[11:6];

    always @(posedge clk or posedge rst) begin
        if (rst) begin
            out_reg_file <= 0;
        end else begin
            out_reg_file[dest] <= sa == 0 ? 0 : reg_file[sa];
        end
    end
endmodule

