    input [31:0] reg_file [0:31],
    output reg [31:0] decoded_instr,
    output reg [31:0] result
);
    wire [4:0] opcode;
    wire [4:0] rs1;
    wire [4:0] rs2;
    wire [4:0] rd;
    wire [31:0] operand1;
    wire [31:0] operand2;

