    assign rs1 = fetched_instr[26:22];
    assign rs2 = fetched_instr[21:17];
    assign rd = fetched_instr[20:16];

    assign operand1 = reg_file[rs1];
    assign operand2 = reg_file[rs2];

    always @(*) begin
        case (opcode)
            // Addition
            5'b000000: result = operand1 + operand2;
