module execute_stage(
    input clk,
    input rst,
    input [31:0] instr,
    input [31:0] reg_file [0:31],
    output reg [31:0] decoded_instr,
    output reg [31:0] result
);
    wire [4:0] rs1, rs2, rd;
    wire [6:0] opcode;
    wire [31:0] op1, op2;

    assign rs1 = instr[25:19];
    assign rs2 = instr[20:14];
    assign rd = instr[18:14];
    assign opcode = instr[31:26];

    always @(posedge clk or posedge rst) begin
        if (rst) begin
            decoded_instr <= 0;
            result <= 0;
        end else begin
            op1 <= reg_file[rs1];
            op2 <= reg_file[rs2];
            case (opcode)
                "0000000": result <= op1 + op2;
                "0000001": result <= op1 - op2;
                default: result <= 0;
            endcase
            decoded_instr <= instr;
        end
    end
endmodule

module writeback_stage(
    input clk,
    input rst,
    input [31:0] instr,
    input [31:0] reg_file [0:31],
    output reg [31:0] out_reg_file [0:31]
);
    wire [4:0] rd;
    assign rd = instr[18:14];
    always @(posedge clk or posedge rst) begin
        if (rst) begin
            out_reg_file <= 0;
        end else begin
            out_reg_file[rd] <= instr[31:0];
        end
    end
endmodule

