// Counter Module
module counter (
    input               clk,
    input               rst_n,
    input               valid_in,
    output              valid_count_reset
);

    reg [2:0] count;

    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            count <= 0;
        end else if (valid_in) begin
            count <= count + 1;
        end
    end

    assign valid_count_reset = (count == 4);

endmodule

// Data Accumulator Module
module data_accumulator (
    input               clk,
    input               rst_n,
    input               valid_in,
    input       [7:0]   data_in,
    output reg  [9:0]   data_out
);

    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            data_out <= 0;
        end else if (valid_in) begin
            data_out <= data_out + data_in;
        end
    end

endmodule

// Valid Output Module
module valid_output (
    input               clk,
    input               rst_n,
    input               valid_in,
    input               valid_count_reset,
    output reg          valid_out
);

    reg [2:0] count;

    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            count <= 0;
            valid_out <= 0;
        end else begin
            if (valid_count_reset) begin
                count <= 0;
                valid_out <= 0;
            end else if (valid_in) begin
                count <= count + 1;
                if (count == 3) begin
                    valid_out <= 1;
                end else begin
                    valid_out <= 0;
                end
            end
        end
    end

endmodule

