module divider_control(
    input wire clk,
    input wire rst,
    input wire opn_valid,
    output reg [7:0] counter,
    output reg [7:0] shift_reg,
    output reg [7:0] quotient,
    output reg [7:0] remainder
);

always @(posedge clk or posedge rst) begin
    if (rst) begin
        counter <= 0;
        shift_reg <= 0;
        quotient <= 0;
        remainder <= 0;
    end else if (opn_valid) begin
        if (counter == 0) begin
            shift_reg <= {8{1'b0}};
            quotient <= {8{1'b0}};
            remainder <= {8{1'b0}};
        end else begin
            shift_reg <= {shift_reg[6:0], 1'b0};
            quotient <= {quotient[6:0], shift_reg[7]};
            remainder <= {remainder[6:0], shift_reg[7]};
        end
        counter <= counter + 1;
    end
end

endmodule

