// Submodule for division
module divider(
    input wire clk,
    input wire rst,
    input wire [7:0] dividend,
    input wire [15:0] divisor,
    output reg [15:0] quotient,
    output reg [15:0] remainder
);
    reg [7:0] counter;
    reg [15:0] shift_reg;

    always @(posedge clk or posedge rst) begin
        if (rst) begin
            counter <= 0;
            shift_reg <= 0;
            quotient <= 0;
            remainder <= 0;
        end else begin
            if (counter < 8) begin
                shift_reg <= {shift_reg[14:0], dividend[counter]};
                counter <= counter + 1;
            end else begin
                if (shift_reg >= divisor) begin
                    shift_reg <= shift_reg - divisor;
                    quotient <= {quotient[14:0], 1'b1};
                end else begin
                    quotient <= {quotient[14:0], 1'b0};
                end
                if (counter == 8) begin
                    remainder <= shift_reg;
                end
            end
        end
    end
endmodule

