module divider(
    input wire clk,
    input wire rst,
    input wire opn_valid,
    input wire [7:0] dividend,
    input wire [7:0] divisor,
    output reg [7:0] shift_reg,
    output reg [7:0] counter,
    output reg [7:0] quotient,
    output reg [7:0] remainder
);
    always @(posedge clk or posedge rst) begin
        if (rst) begin
            shift_reg <= 0;
            counter <= 0;
            quotient <= 0;
            remainder <= 0;
        end else if (opn_valid) begin
            if (counter == 0) begin
                shift_reg <= dividend;
                counter <= 8;
            end else begin
                shift_reg <= {shift_reg[6:0], 1'b0};
                counter <= counter - 1;
                if (shift_reg >= divisor) begin
                    shift_reg <= shift_reg - divisor;
                    quotient <= {quotient[6:0], 1'b1};
                end else begin
                    quotient <= {quotient[6:0], 1'b0};
                end
                if (counter == 1) begin
                    remainder <= shift_reg;
                end
            end
        end
    end
