        if (rst) begin
            result <= 0;
            res_valid <= 0;
        end else if (res_ready) begin
            if (counter == 0) begin
                result <= {quotient, remainder};
                res_valid <= 1;
            end else begin
                result <= {shift_reg, quotient};
                res_valid <= 0;
            end
        end
    end
