    input [15:0] B;      // 16-bit input operand B
    output [15:0] Diff;   // 16-bit output representing the difference of A and B
    output B_out;      // Borrow-out

    wire [15:0] S;
    wire B_out_temp;

    // Calculate the difference
    assign S = A - B;
    assign Diff = S;

    // Calculate the borrow-out
    assign B_out = (A < B);

endmodule

