        .MUL2_DIV_CLK(MUL2_DIV_CLK)
    ) u_clk_adjustment (
        .clk(clk),
        .clk_average(clk_average),
        .clk_adjustment(clk_adjustment)
    );

    // Generate the divided clock signal
    assign clk_div = clk_counter[MUL2_DIV_CLK] | clk_adjustment[MUL2_DIV_CLK];

endmodule

