    input [7:0]    clk_average,
    output reg [7:0]   clk_adjustment
);

    parameter MUL2_DIV_CLK = 7;

    always @(negedge clk) begin
        clk_adjustment <= {clk_average[MUL2_DIV_CLK-1], clk_average[MUL2_DIV_CLK-1:0]};
    end

