module decoder(
    input [1:0] op_code,
    output [3:0] a_result,
    output [3:0] l_result
);

    always @(*) begin
        case(op_code)
            2'b00: begin
                a_result = 4'b0000;
                l_result = 4'b0000;
            end
            2'b01: begin
                a_result = data_a + data_b;
                l_result = 4'b0000;
            end
            2'b10: begin
                a_result = data_a - data_b;
                l_result = 4'b0000;
            end
            2'b11: begin
                a_result = 4'b0000;
                l_result = data_a & data_b;
            end
        endcase
    end

endmodule

module arithmetic_logic(
