    output [3:0] l_result
);

    always @(*) begin
        case(op_code)
            2'b00: begin
                result = data_a + data_b;
                l_result = 4'b0000;
            end
            2'b01: begin
                result = data_a - data_b;
                l_result = 4'b0000;
            end
            2'b10: begin
                result = 4'b0000;
                l_result = data_a & data_b;
            end
        endcase
    end

endmodule

