input [63:0] A;       // 64-bit input A
    input [63:0] B;       // 64-bit input B
    output [63:0] D;       // 64-bit output D = A - B
    output B_out;             // Borrow-out output

    wire [63:0] D1, D2;
    wire B_out1, B_out2;

    // Instantiate the 32-bit subtractor module
    sub_32bit sub0(.A(A[63:32]), .B(B[63:32]), .D(D1), .B_out(B_out1));
    sub_32bit sub1(.A(A[31:0]), .B(B[31:0]), .D(D2), .B_out(B_out2));

    // Calculate D = A - B
    assign D = D1 - D2;
    assign B_out = B_out1 | B_out2;

endmodule

module sub_32bit(A, B, D, B_out); ASSISTANT: 
    input [31:0] A;       // 32-bit input A
    input [31:0] B;       // 32-bit input B
    output [31:0] D;       // 32-bit output D = A - B
    output B_out;             // Borrow-out output

    wire [31:0] D1, D2;
    wire B_out1, B_out2;

    // Instantiate the 16-bit subtractor module
    sub_16bit sub0(.A(A[31:16]), .B(B[31:16]), .D(D1), .B_out(B_out1));
    sub_16bit sub1(.A(A[15:0]), .B(B[15:0]), .D(D2), .B_out(B_out2));

    // Calculate D = A - B
    assign D = D1 - D2;
    assign B_out = B_out1 | B_out2;

endmodule

module sub_16bit(A, B, D, B_out); ASSISTANT: 
    input [15:0] A;       // 16-bit input A
    input [15:0] B;       // 16-bit input B
    output [15:0] D;       // 16-bit output D = A - B
    output B_out;             // Borrow-out output

    wire [15:0] D1, D2;
    wire B_out1, B_out2;

    // Instantiate the 8-bit subtractor module
    sub_8bit sub0(.A(A[15:8]), .B(B[15:8]), .D(D1), .B_out(B_out1));
    sub_8bit sub1(.A(A[7:0]), .B(B[7:0]), .D(D2), .B_out(B_out2));

    // Calculate D = A - B
    assign D = D1 - D2;
    assign B_out = B_out1 | B_out2;

endmodule

module sub_8bit(A, B, D, B_out); ASSISTANT: 
    input [7:0] A;       // 8-bit input A
    input [7:0] B;       // 8-bit input B
    output [7:0] D;       // 8-bit output D = A - B
    output B_out;             // Borrow-out output

    wire [7:0] D1, D2;
    wire B_out1, B_out2;

    // Instantiate the 4-bit subtractor module
    sub_4bit sub0(.A(A[7:4]), .B(B[7:4]), .D(D1), .B_out(B_out1));
    sub_4bit sub1(.A(A[3:0]), .B(B[3:0]), .D(D2), .B_out(B_out2));

    // Calculate D = A - B
    assign D = D1 - D2;
    assign B_out = B_out1 | B_out2;

endmodule

module sub_4bit(A, B, D, B_out); ASSISTANT: 
    input [3:0] A;       // 4-bit input A
    input [3:0] B;       // 4-bit input B
    output [3:0] D;       // 4-bit output D = A - B
    output B_out;             // Borrow-out output

    wire [3:0] D1, D2;
    wire B_out1, B_out2;

    // Instantiate the 2-bit subtractor module
    sub_2bit sub0(.A(A[3:2]), .B(B[3:2]), .D(D1), .B_out(B_out1));
    sub_2bit sub1(.A(A[1:0]), .B(B[1:0]), .D(D2), .B_out(B_out2));

    // Calculate D = A - B
    assign D = D1 - D2;
    assign B_out = B_out1 | B_out2;

endmodule

module sub_2bit(A, B, D, B_out); ASSISTANT: 
    input [1:0] A;       // 2-bit input A
    input [1:0] B;       // 2-bit input B
    output [1:0] D;       // 2-bit output D = A - B
    output B_out;             // Borrow-out output

    wire [1:0] D1, D2;
    wire B_out1, B_out2;

    // Instantiate the 1-bit subtractor module
    sub_1bit sub0(.A(A[1]), .B(B[1]), .D(D1), .B_out(B_out1));
    sub_1bit sub1(.A(A[0]), .B(B[0]), .D(D2), .B_out(B_out2));

    // Calculate D = A - B
    assign D = D1 - D2;
    assign B_out = B_out1 | B_out2;

endmodule

module sub_1bit(A, B, D, B_out); ASSISTANT: 
    input A;       // 1-bit input A
    input B;       // 1-bit input B
    output D;       // 1-bit output D = A - B
    output B_out;             // Borrow-out output

    wire D1, D2;
    wire B_out1, B_out2;

    // Calculate D = A - B
    assign D = A ^ B;
    assign B_out = A & B;

endmodule

