module arithmetic_logic(
    input [3:0] data_a,
    input [3:0] data_b,
    input [3:0] a_result,
    input [3:0] b_result,
    input [3:0] c_result,
    input [3:0] d_result,
    output [3:0] result
);

    wire [3:0] add_result;
    wire [3:0] sub_result;
    wire [3:0] and_result;
    wire [3:0] or_result;

    // Addition
    adder add (
        .a(data_a),
        .b(data_b),
        .sum(add_result)
    );

    // Subtraction
    subtractor sub (
        .a(data_a),
        .b(data_b),
        .diff(sub_result)
    );

    // AND
    and_gate and_gate (
        .a(data_a),
        .b(data_b),
        .result(and_result)
    );

    // OR
    or_gate or_gate (
        .a(data_a),
        .b(data_b),
        .result(or_result)
    );

    assign result = (a_result == 4'b0001) ? add_result :
                  (b_result == 4'b0010) ? sub_result :
                  (c_result == 4'b0100) ? and_result :
                  (d_result == 4'b1000) ? or_result : 4'b0000;

endmodule

