    // Calculate borrow bits
    assign borrow[3] = B_in;
    assign borrow[2] = (B[3] & ~A[3]) | (~B[3] & B_in);
    assign borrow[1] = (B[2] & ~A[2]) | (~B[2] & borrow[2]);
    assign borrow[0] = (B[1] & ~A[1]) | (~B[1] & borrow[1]);

    // Calculate difference
    assign D[3] = A[3] ^ B[3] ^ B_in;
    assign D[2] = A[2] ^ B[2] ^ borrow[2];
    assign D[1] = A[1] ^ B[1] ^ borrow[1];
    assign D[0] = A[0] ^ B[0] ^ borrow[0];

    // Calculate borrow-out
    assign B_out = (B[3] & ~A[3]) | (~B[3] & B_in);
