    input [16:1] B;        // 16-bit input B
    output [16:1] D;       // 16-bit output difference
    output B_out;        // Borrow-out output

    wire [16:1] borrow;

    // Instantiate 4-bit full subtractors
    full_subtractor sub1 (.A(A[15:12]), .B(B[15:12]), .D(D[15:12]), .B_out(borrow[15:12]));
    full_subtractor sub2 (.A(A[11:8]), .B(B[11:8]), .D(D[11:8]), .B_out(borrow[11:8]));
    full_subtractor sub3 (.A(A[7:4]), .B(B[7:4]), .D(D[7:4]), .B_out(borrow[7:4]));
    full_subtractor sub4 (.A(A[3:0]), .B(B[3:0]), .D(D[3:0]), .B_out(borrow[3:0]));

    // Calculate borrow-out
    assign B_out = borrow[15] & borrow[11] & borrow[7] & borrow[3];

