wire [31:0] add_r, sub_r, and_r, or_r, xor_r, nor_r;
    wire [31:0] slt_r, sltu_r, sll_r, srl_r, sra_r, sllv_r, srlv_r, sra_r_v;
    wire [31:0] lui_r;

    // Instantiate the submodules for each operation
    add add(.a(a), .b(b), .r(add_r));
    sub subtract(.a(a), .b(b), .r(sub_r));
    and_gate and_gate(.a(a), .b(b), .r(and_r));
    or_gate or_gate(.a(a), .b(b), .r(or_r));
    xor_gate xor_gate(.a(a), .b(b), .r(xor_r));
    nor_gate nor_gate(.a(a), .b(b), .r(nor_r));
    slt slt(.a(a), .b(b), .r(slt_r));
    sltu sltu(.a(a), .b(b), .r(sltu_r));
    sll sll(.a(a), .b(b), .r(sll_r));
    srl srl(.a(a), .b(b), .r(srl_r));
    sra sra(.a(a), .b(b), .r(sra_r));
    sllv sllv(.a(a), .b(b), .r(sllv_r));
    srlv srlv(.a(a), .b(b), .r(srlv_r));
    sra_v sra_v(.a(a), .b(b), .r(sra_r_v));
    lui lui(.a(a), .b(b), .r(lui_r));

    // Select the result based on the aluc signal
    assign r = (aluc == 6'b100000) ? add_r :
                  (aluc == 6'b100001) ? add_r :
                  (aluc == 6'b100010) ? sub_r :
                  (aluc == 6'b100011) ? sub_r :
                  (aluc == 6'b100100) ? and_r :
                  (aluc == 6'b100101) ? or_r :
                  (aluc == 6'b100110) ? xor_r :
                  (aluc == 6'b100111) ? nor_r :
                  (aluc == 6'b101010) ? slt_r :
                  (aluc == 6'b101011) ? sltu_r :
                  (aluc == 6'b000000) ? sll_r :
                  (aluc == 6'b000010) ? srl_r :
                  (aluc == 6'b000011) ? sra_r :
                  (aluc == 6'b000100) ? sllv_r :
                  (aluc == 6'b000110) ? srlv_r :
                  (aluc == 6'b000111) ? sra_r_v :
                  (aluc == 6'b001111) ? lui_r :
                  32'b0;

    // Compute the flags
    assign zero = (r == 0);
    assign carry = (aluc == 6'b100001) ? (a[31] & b[31]) :
                    (aluc == 6'b100010) ? (a[31] ^ b[31]) :
                    (aluc == 6'b100011) ? (a[31] ^ b[31]) :
                    (aluc == 6'b100100) ? (a[31] | b[31]) :
                    (aluc == 6'b100101) ? (a[31] | b[31]) :
                    (aluc == 6'b100110) ? (a[31] | b[31]) :
                    (aluc == 6'b100111) ? (a[31] | b[31]) :
                    (aluc == 6'b101010) ? (a[31] | b[31]) :
                    (aluc == 6'b101011) ? (a[31] | b[31]) :
                    (aluc == 6'b000000) ? (a[31] | b[31]) :
                    (aluc == 6'b000010) ? (a[31] | b[31]) :
                    (aluc == 6'b000011) ? (a[31] | b[31]) :
                    (aluc == 6'b000100) ? (a[31] | b[31]) :
                    (aluc == 6'b000110) ? (a[31] | b[31]) :
                    (aluc == 6'b000111) ? (a[31] | b[31]) :
                    (aluc == 6'b001111) ? (a[31] | b[31]) :
                    1'b0;

    assign negative = (r[31] == 1);
    assign overflow = (aluc == 6'b101010) ? (a[31] ^ r[31]) :
                      (aluc == 6'b101011) ? (a[31] ^ r[31]) :
                      (aluc == 6'b101100) ? (a[31] ^ r[31]) :
                      (aluc == 6'b101101) ? (a[31] ^ r[31]) :
                      (aluc == 6'b101110) ? (a[31] ^ r[31]) :
                      (aluc == 6'b101111) ? (a[31] ^ r[31]) :
                      (aluc == 6'b000000) ? (a[31] ^ r[31]) :
                      (aluc == 6'b000010) ? (a[31] ^ r[31]) :
                      (aluc == 6'b000011) ? (a[31] ^ r[31]) :
                      (aluc == 6'b000100) ? (a[31] ^ r[31]) :
                      (aluc == 6'b000110) ? (a[31] ^ r[31]) :
                      (aluc == 6'b000111) ? (a[31] ^ r[31]) :
                      1'b0;

    assign flag = (aluc == 6'b101010) ? (a[31] ^ r[31]) :
                  (aluc == 6'b101011) ? (a[31] ^ r[31]) :
                  (aluc == 6'b101100) ? (a[31] ^ r[31]) :
                  (aluc == 6'b101101) ? (a[31] ^ r[31]) :
                  (aluc == 6'b101110) ? (a[31] ^ r[31]) :
                  (aluc == 6'b101111) ? (a[31] ^ r[31]) :
                  1'b0;
endmodule

