wire [31:0] sum_out;
    wire [31:0] sub_out;
    wire [31:0] and_out;
    wire [31:0] or_out;

    // Instantiate the submodules
    add_submodule add_submodule (
        .a(a),
        .b(b),
        .sum(sum_out)
    );

    subtractor_module subtractor_module (
        .a(a),
        .b(b),
        .sub_out(sub_out)
    );

    assign and_out = a & b;
    assign or_out = a | b;

    assign add_out = a | b;

    assign result = (op_sel == 00) ? sum_out
                (op_sel == 01) ? sub_out
                (op_sel == 10) ? and_out
                op_sel == 11 ? or_out : 32'b0;

endmodule

// Adder module
module add_submodule(
    input [31:0] a,
    input [31:0] b,
    output [31:0] sum
);
    assign sum = a + b;
endmodule

module subtractor_module(
    input [31:0] a,
    input [31:0] b,
    output [31:0] sub_out
);
    assign sub_out = a - b;
endmodule

module and_module(
    input [31:0] a,
    input [31:0] b,
    output [31:0] and_out
);
    assign and_out = a & b;
endmodule

module or_module(
    input [31:0] a,
    input [31:0] b,
    output [31:0] or_out
);
    assign or_out = a | b;
endmodule

