    );

    // Instantiate the final output
    assign result = (op_sel == 00) ? sum_out : (op_sel == 01) ? diff_out : (op_sel == 11) ? or_out : 0;

endmodule

module adder(
    input [31:0] a,
    input [31:0] b,
    output [31:0] sum
);
    assign sum = a + b;
endmodule

module subtractor(
    input [31:0] a,
    input [31:0] b,
    output [31:0] diff
);
    assign diff = a - b;
endmodule

module and_module(
    input [31:0] a,
    input [31:0] b,
    output [31:0] and_out
);
    assign and_out = a & b;
endmodule

module or_module(
    input [31:0] a,
    input [31:0] b,
    output [31:0] or_out
);
    assign or_out = a | b;
endmodule

