input [3:0] a;
    input [3:0] b;
    input [1:0] op;
    output [3:0] result;
    output carry_borrow;

    wire [3:0] and_result;
    wire [3:0] or_result;
    wire [3:0] add_result;
    wire [3:0] sub_result;

    // Bitwise AND
    wire [3:0] and_out;
    assign and_out = a & b;

    // Bitwise OR
    wire [3:0] or_out;
    assign or_out = a | b;

    // Bitwise addition
    wire [3:0] add_out;
    assign add_out = a + b;

    // Bitwise subtraction
    wire [3:0] sub_out;
    assign sub_out = a - b;

    // 2-bit adder
    always @(*) begin
        case (op)
            2'b00: and_out
            2'b01: or_out
            2'b10: add_out
            2'b11: sub_out
        endcase
    end

    // 2-bit adder
    always @(*) begin
        case (op)
            2'b00: add_out
            2'b01: or_out
            2'b10: add_out
            2'b11: sub_out
        endcase
        end

