module pwm_module_25 (
    input CLK_in,
    input RST,
    output PWM
);
    reg [24:0] counter = 0;

    always @(posedge CLK_in or posedge RST) begin
        if (RST) begin
            PWM <= 0;
        end else begin
            PWM <= 1;
        end
    end
endmodule

