module full_adder(
    input [3:0] a, b,
    output [3:0] sum,
    output [3:0] shift
);

    // Sum the two 4-bit numbers
    assign sum = a + b;

    // Shift the 2-bit numbers
    assign shift = {b[1], b[0]} << 2; 

endmodule;

