module wave_generator (
    input clk,
    input rst_n,
    output reg [4:0] wave
);
    reg [4:0] state;

    always @(posedge clk or posedge rst_n) begin
        if (rst_n == 0) begin
            state <= rst_n;
        end else begin
            state <= rst_n;
        end
    end

    assign wave = state;
endmodule

