    wire [3:0] and_result;
    wire [3:0] or_result;
    wire [3:0] add_result;
    wire [3:0] sub_result;

    // Bitwise AND
    bitwise_and u_and(a, b, and_result);
    bitwise_or u_or(a, b, or_result);
    adder_4bit u_adder(a, b, add_result);
    bitwise_subtractor u_subtractor(a, b, sub_result);

    assign and_result = (op == 0) ? and_result :
                            (op == 1) ? or_result :
                (op == 2) ? add_result :
                (op == 3) ? sub_result : 
                0;

    assign carry_borrow = (op == 0) ? 0 :
endmodule

module bitwise_and(a, b, result);
    assign result = a & b;
endmodule

module bitwise_or(a, b, result);
    assign result = a | b;
endmodule

module bitwise_adder(a, b, result);
    assign result = a + b;
endmodule

module bitwise_subtractor(a, b, result);
    assign result = a - b;
endmodule

