wire [7:0] temp;

    // Instantiate the three flip-flops
    y0 u0(.clk(clk), .d(d), .sel(sel), .q(temp));
    y1 u1(.clk(clk), .d(d), .sel(sel), .q(temp));
    y2 u2(.clk(clk), .d(d), .sel(sel), .q(temp));

    // Shift the selected output
    assign q = temp[sel];

endmodule

module my_d(
    input clk,
    input [7:0] d,
    input [1:0] sel,
    output reg [7:0] q
);
    always @(posedge clk) begin
        if (sel == 0)
            q <= d;
        else
            q <= d << sel;
    end
endmodule

wire [7:0] shift_reg [7:0];

    // Instantiating the flip-flip module
    ff_flip u0 (
        .clk(clk),
        .d(d),
        .sel(sel),
        .q(shift_reg)
    );

    // Instantiate the shift register
    always @(posedge clk) begin
        shift_reg <= shift_reg;
    end

    // Instantiate the multiplexer
    always @(posedge clk) begin
        q <= {shift_reg, {sel}};
    end

endmodule

module ff_flip (
    input clk,
    input [7:0] d,
    input [1:0] sel,
    output reg [7:0] q
);

    always @(posedge clk) begin
        q <= {d, {sel}};
    end

endmodule

