    assign result = (op_code == 1) ? sum : (op_code == 2) ? diff : 0;

endmodule

module adder(
    input wire [31:0] a,
    input wire [31:0] b,
    output wire [31:0] sum
);
    assign sum = a + b;
endmodule

module subtractor(
    input wire [31:0] a,
    input wire [31:0] b,
    output wire [31:0] diff
);
    assign diff = a - b;
endmodule

module and_logic(
    input wire [31:0] a,
    input wire [31:0] b,
    output wire [31:0] and_result
);
    assign and_result = a & b;
endmodule

module or_logic(
    input wire [31:0] a,
    input wire [31:0] b,
    output wire [31:0] or_result
);
    assign or_result = a | b;
endmodule

