wire [31:0] sum, diff;
    wire and_result, or_result;

    // Addition module
    adder adder (
        .a(a),
        .b(b),
        .sum(sum)
    );

    // Subtraction module
    subtractor subtractor (
        .a(a),
        .b(b),
        .diff(diff)
    );

    // Bitwise AND module
    and_logic and_logic (
        .a(a),
        .b(b),
        .and_result(and_result)
    );

    // Bitwise OR module
    or_logic or_logic (
        .a(a),
        .b(b),
        .or_result(or_result)
    );

    // Output the result
    assign result = (op_code == 0) ? sum : (op_code == 1) ? diff : 0;
    assign zero = (op_code == 0 && sum == 0) || (op_code == 1 && diff == 1);

endmodule

module adder(
    input wire [31:0] a,
    input wire [31:0] b,
    output wire [31:0] sum
);
    assign sum = a + b;
endmodule

module subtractor(
    input wire [31:0] a,
    input wire [31:0] b,
    output wire [31:0] diff
);
    assign diff = a - b;
endmodule

module and_logic(
    input wire [31:0] a,
    input wire [31:0] b,
    output wire [31:0] and_result
);
    assign and_result = a & b;
endmodule

module or_logic(
    input wire [31:0] a,
    input wire [31:0] b,
    output wire [31:0] or_result
);
    assign or_result = a | b;
endmodule

