wire [31:0] res_reg_file;

  // Fetch stage
  fetch_stage fetch_stage (
    .clk(clk),
    .rst(rst),
    .instr_mem(instr_mem),
    .reg_file(reg_file)
  );

  // Decode stage
  decode_stage decode_stage (
    .rs1(reg_file[0]),
    .rs2(reg_file[1]),
    .rd(rd),
    .op(instr_mem[0]),
    .res(res_reg_file)
  );

  // Memory stage
  memory_stage memory_stage (
    .clk(clk),
    .rst(rst),
    .rd(rd),
    .op(instr_mem[0]),
    .res(res_reg_file)
  );

  // Write back to register file
  write_stage write_stage (
    .clk(clk),
    .rst(rst),
    .rd(rd),
    .op(instr_mem[0]),
    .res(res_reg_file)
  );

endmodule

module fetch_stage (
  input clk,
  input rst,
  input [31:0] instr_mem [31:0],
  input [31:0] reg_file [31:0]
);

  reg [31:0] res_reg_file;

  always @(posedge clk or posedge rst) begin
    if (rst) begin
      res_reg_file <= 0;
    end else begin
      res_reg_file <= instr_mem[0];
    end
  end

endmodule

