    smoic_logic smoke_detector (
        .smoeector (smoeector),
        .smoo (smoo) (smoo)
    );

    // Instantiate the final alarm logic
    or_logic alarm_logic (
        .smoeector (smoeector),
        .smoo (smoo),
        .smoo (smoo)
    );

endmodule

module compare_temp (
    input [7:0] temp_input [7:0],
    input [7:0] threshold [7:0],
    output compare_temp
);

    // Compare the temperature input to the threshold
    if (temp_input > threshold) (
        compare_temp = 1
    );

endmodule

module or_logic (
    input [0] sigma_input,
    input [0] smoke_input,
    output moo
);

    // Perform the OR operation
    assign moo = sigma_input | smoke_input;

endmodule

