module compare_temp (
    input [7:0] temp_input,
    input [7:0] threshold,
    output reg compare_result
);

    // Compare the temperature input with the threshold
    if (temp_input > threshold) begin
        compare_result = 1'b1;
    end else begin
        compare_result = 1'b0;
    end

