module fetch_stage(
    input clk,
    input rst,
    input [31:0] instr_mem [0:31],
    input [31:0] reg_file [0:31],
    output reg_file [31:0],
    output inst_op1,
    output inst_op2
);

    reg [31:0] pc;
    wire [31:0] inst_op1_out, inst_op2_out;

    // Fetch stage
    always @(posedge clk or posedge rst) begin
        if (rst) begin
            pc <= 0;
        end else begin
            if (clk) begin
                pc <= pc + 1;
                end
            end
            if (pc == 0) begin
                inst_op1 <= inst_op1_out;
                inst_op2 <= inst_op2_out;
                reg_file <= inst_op1_out;
                end
            if (pc == 1) begin
                inst_op1 <= inst_op1_out;
                inst_op2 <= inst_op2_out;
                reg_file <= inst_op1_out;
                end
            else begin
                inst_op1 <= inst_op1_out;
                inst_op2 <= inst_op2_out;
                reg_file <= inst_op1_out;
            end
        end
    end

    endmodule

