    );

    reg [31:0] reg_file [31:0];

    always @(posedge clk or posedge rst) begin
        if (rst) begin
            inst_op1_out <= inst_op1;
            inst_op2_out <= inst_op2_out;
            reg_file <= inst_op1_out;
        end else begin
            inst_op1_out <= inst_op1_out;
            inst_op2_out <= inst_op2_out;
            reg_file <= inst_op2_out;
        end
    end

endmodule

