module abs_value(
    input wire clk,
    input wire [7:0] dividend,
    input wire [7:0] divisor,
    input wire sign,
    output reg [15:0] result
);

    always @(posedge clk) begin
        if (sign) begin
            result = sign ? ~divid : dividend;
        end else begin
            result = sign ? ~divid : dividend;
        end
    end

endmodule

