module div_32bit(
    input wire [31:0] A,   // 32-bit dividend
    input wire [15:0] B,   // 16-bit divisor
    output wire [31:0] result,  // 32-bit quotient
    output wire [31:0] odd
)
    wire [31:0] dividend,  // 32-bit remainder
    wire [31:0] divisor_;

    assign dividend = A;
    assign divisor_ = B;

    assign result = A >>> B;
    assign odd = A >>> B;

endmodule

module div_32bit(
