module JC_register(
    input                clk,
    input                rst_n,
    output                Q
);

    reg [63:0] current_state;

    always @(posedge clk or posedge rst_n) begin
        if (rst_n) begin
            current_state <= 0;
        end else begin
            current_state <= current_state + 1;
        end
    end

endmodule

module JC_logic(
    input            clk,
    input            rst_n,
    output                Q
);

    reg [63:0] next_state;

    always @(posedge clk or posedge rst_n) begin
        if (rst_n) begin
            next_state <= 0;
        end else begin
            next_state <= next_state + 1;
        end
    end

endmodule

