module JC_register(
    input                clk,
    input                rst_n,
    output    reg [63:0]    Q
);

    always @(*) begin
        if (!rst_n)) begin
            Q <= 0;
        end else begin
            Q <= Q + 1;
        end
    end

endmodule

module JC_logic(
    input    clk,
    input    rst_n,
    output    reg [63:0]    Q
);

    always @(*) begin
        if (!rst_n)) begin
            Q <= 0;
        end else begin
            Q <= Q + 1;
        end
    end

endmodule

reg [63:0] current_state;
    wire [63:0] next_state;

    // Instantiate the current state register
    always @(clk) begin
        if (rst_n) begin
            current_state <= 0;
        end else begin
            current_state <= current_state + 1;
        end
    end

    // Instantiate the next state
    JC_register #(clk, rst_n, current_state) (
        .clk(clk),
        .rst_n(rst_n),
        .current_state(current_state),
        .next_state(next_state)
    );

    // Instantiate the next state
    JC_logic #(clk, rst_n, current_state, next_state) (
        .clk(clk),
        .rst_n(rst_n),
        .current_state(current_state),
        .next_state(next_state)
    );

    // Output the next state
    assign Q = next_state;

endmodule

module JC_register(
    input                clk,
    input                rst_n,
    input                current_state,
    output    next_state
);

    reg [63:0] next_state;

    always @(clk) begin
        if (rst_n) begin
            next_state <= 0;
        end else begin
            next_state <= current_state + 1;
        end
    end

endmodule

