module JC_register(
    input                clk,
    input                rst_n,
    output    [63:0]    current_state
);

    // Instantiate the submodule for storing the current state
    reg [63:0] current_state;

    // Instantiate the submodule for resetting the current state
    initial begin
        if (rst_n) begin
            current_state <= 0;
        end
    end

    // Instantiate the submodule for calculating the next state
    always @(*) begin
        // Resetting the current state
        current_state <= current_state;
    end

endmodule

