    shift_8_bit shift_8(Y, temp);

    // Sum up to get final product
    sum_8_bit sum_8(temp, P);

endmodule

module add_shift(input [7:0] X, Y, out);
    wire [7:0] sum;
    wire [7:0] shift;
    wire [7:0] temp;

    // Add X to temp
    assign sum = X;

    // Shift X to accumulator
    assign temp = X;
    assign shift = Y;

    // Sum up to get final product
    assign P = temp << shift;
endmodule

module shift_8_bit(input [7:0] Y, out);
    wire [7:0] shift;
    wire [7:0] temp;

    // Shift X to accumulator
    assign shift = Y;

    // Sum up to get final product
    assign temp = Y;

endmodule

input [15:0] X;
    input [7:0] Y;
    output [15:0] P;

    wire [15:0] temp;

    // Add and shift the multiplic adder
    add_shift add_shift(X, Y, temp);

    // Shift the adder output to the multiplic adder
    add_shift add_shift(temp, Y, P);

    // Shift the adder output to the multiplic adder
    add_shift add_shift(P, Y, P);

endmodule

module add_shift(
    input [15:0] X,
    input [7:0] Y,
    output [15:0] P
);

    // Add the adder output to the multiplic adder
    wire [15:0] sum;
    assign sum = X + Y;

    // Shift the adder output to the multiplic adder
    assign P = X % 16;

endmodule

